import React, { useEffect } from 'react';
import { Howl } from 'howler';

// Sonidos del juego
const clickSound = new Howl({
  src: [
    'data:audio/mpeg;base64,'
  ],
  volume: 0.5
});

const comboSound = new Howl({
  src: [
    'data:audio/mpeg;base64,'
  ],
  volume: 0.7
});

const upgradeSound = new Howl({
  src: [
    'data:audio/mpeg;base64,'
  ],
  volume: 0.6
});

const achievementSound = new Howl({
  src: [
    'data:audio/mpeg;base64,'
  ],
  volume: 0.8
});

// Hook para usar sonidos
export const useGameSound = () => {
  const playClickSound = () => {
    clickSound.play();
  };

  const playComboSound = () => {
    comboSound.play();
  };

  const playUpgradeSound = () => {
    upgradeSound.play();
  };

  const playAchievementSound = () => {
    achievementSound.play();
  };

  return {
    playClickSound,
    playComboSound,
    playUpgradeSound,
    playAchievementSound
  };
};